mapper.select:
select users.* from ( select rownum user_order , ulist.* from ( select u.user_no , u.user_id , u.user_name , u.phone , u.email , u.is_leave from user_tb u where 1=1
and ( user_id LIKE '%' || #{search} || '%' or user_name LIKE '%' || #{search} || '%' or phone LIKE '%' || #{search} || '%' )
order by u.user_no desc ) ulist ) users where user_order between #{startNo} and #{endNo}

select flist.* from ( select rownum f_order , fs.* from ( select f.farmer_no , f.farmer_id , f.name , f.phone , f.email , f.is_leave , farm.farm_no from farmer f JOIN farm ON f.farmer_no = farm.farmer_no where f.is_confirm = 1
and ( f.farmer_id LIKE '%' || #{search} || '%' or f.name LIKE '%' || #{search} || '%' )
order by f.farmer_no desc )fs )flist where flist.f_order between #{startNo} and #{endNo}

select falist.* from( select rownum farm_order , fa.* from ( select f.farmer_no , f.farmer_id , f.name , f.application_date , farm.farm_no , farm.farm_name from farmer f JOIN farm ON f.farmer_no = farm.farmer_no where is_confirm = 0
and ( farm.farm_name LIKE '%' || #{search} || '%' or f.farmer_id LIKE '%' || #{search} || '%' or f.name LIKE '%' || #{search} || '%' )
order by f.application_date , f.farmer_no )fa )falist where farm_order between #{startNo} and #{endNo}
