mapper.select:
select p_paging.* from ( select rownum p_order ,p_list.* from ( select p.product_no , p.name , p.kind , p.is_seasonal_food , p.product_sas , p.farmer_no , f.farmer_id from product p JOIN farmer f ON p.farmer_no = f.farmer_no where 1=1
and ( p.name like '%' || #{search} || '%' or p.kind like '%' || '체리' || '%' or f.farmer_id like '%' || #{search} || '%' )
ORDER BY p.product_no desc )p_list )p_paging where p_paging.p_order between #{startNo} and #{endNo}
