mapper.select:
  	select
		 farm_diary_no, title, enroll_date, 
		 youtube_link, content, farmer_no 
		from(
			select rownum rnum, n1.* 
			from(
				select * 
				from farm_diary
				order by farm_diary_no desc
			)n1
		)where rnum between #{start} and #{end}

select farm_diary_no, title, hit, enroll_date, youtube_link, content, farmer_no
from farm_diary
where farm_diary_no = #{farmDiaryNo}
